import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Play, Upload, Map, AlertCircle } from 'lucide-react';
import Papa from 'papaparse';

const OaxacaCovidSimulator = () => {
  const [selectedYear, setSelectedYear] = useState('2020');
  const [selectedRegion, setSelectedRegion] = useState('Valles Centrales');
  const [selectedMetric, setSelectedMetric] = useState('confirmados');
  const [isAnimating, setIsAnimating] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(0);
  const [showMap, setShowMap] = useState(true);
  const [csvData, setCsvData] = useState({});
  const [filesUploaded, setFilesUploaded] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const regions = ['Valles Centrales', 'Istmo', 'Costa', 'Mixteca', 'Sierra Norte', 'Sierra Sur', 'Ca√±ada', 'Papaloapan'];
  const years = ['2020', '2021', '2022', '2023'];
  const metrics = [
    { value: 'confirmados', label: 'Casos Confirmados', color: '#ef4444', key: 'Casos Confirmados' },
    { value: 'sospechosos', label: 'Casos Sospechosos', color: '#f59e0b', key: 'Casos Sospechosos' },
    { value: 'recuperacion', label: 'Recuperaciones', color: '#10b981', key: 'Recuperacion' },
    { value: 'defunciones', label: 'Defunciones', color: '#6b7280', key: 'Defunciones' }
  ];

  const regionCoordinates = {
    'Valles Centrales': { x: 180, y: 140 },
    'Istmo': { x: 280, y: 180 },
    'Costa': { x: 100, y: 200 },
    'Mixteca': { x: 120, y: 80 },
    'Sierra Norte': { x: 200, y: 60 },
    'Sierra Sur': { x: 160, y: 180 },
    'Ca√±ada': { x: 220, y: 100 },
    'Papaloapan': { x: 260, y: 80 }
  };

  const meses = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 
                 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];

  // Cargar CSVs autom√°ticamente al iniciar
  useEffect(() => {
    const loadAllCSVs = async () => {
      setLoading(true);
      const allData = {};
      const filesLoaded = {};

      for (const year of years) {
        try {
          const filename = `/data/covid_oaxaca_${year}_mensual.csv`;
          const response = await fetch(filename);
          const data = await response.text();

          Papa.parse(data, {
            header: true,
            skipEmptyLines: true,
            dynamicTyping: true,
            complete: (results) => {
              const processedData = processCSVData(results.data);
              allData[year] = processedData;
              filesLoaded[year] = `covid_oaxaca_${year}_mensual.csv`;
            }
          });
        } catch (err) {
          console.log(`No se encontr√≥ el archivo para ${year}: ${err.message}`);
        }
      }

      setCsvData(allData);
      setFilesUploaded(filesLoaded);
      setLoading(false);
    };

    loadAllCSVs();
  }, []);

  const handleFileUpload = (event, year) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError('');

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      dynamicTyping: true,
      complete: (results) => {
        try {
          const processedData = processCSVData(results.data);

          setCsvData(prev => ({
            ...prev,
            [year]: processedData
          }));

          setFilesUploaded(prev => ({
            ...prev,
            [year]: file.name
          }));

          setLoading(false);
        } catch (err) {
          setError(`Error procesando ${year}: ${err.message}`);
          setLoading(false);
        }
      },
      error: (err) => {
        setError(`Error leyendo archivo: ${err.message}`);
        setLoading(false);
      }
    });
  };

  const processCSVData = (data) => {
    const regionData = {};

    // Inicializar todas las regiones
    regions.forEach(region => {
      regionData[region] = [];
    });

    // Procesar cada fila del CSV
    data.forEach(row => {
      // Normalizar nombres de columnas (eliminar espacios)
      const normalizedRow = {};
      Object.keys(row).forEach(key => {
        normalizedRow[key.trim()] = row[key];
      });

      const region = normalizedRow['Region'] || normalizedRow['region'];
      const mes = normalizedRow['Mes'] || normalizedRow['mes'];

      if (region && mes && regions.includes(region)) {
        const monthData = {
          mes: mes,
          confirmados: parseInt(normalizedRow['Casos Confirmados'] || normalizedRow['Casos Confir'] || normalizedRow['confirmados'] || 0),
          sospechosos: parseInt(normalizedRow['Casos Sospechosos'] || normalizedRow['Casos Sospe'] || normalizedRow['sospechosos'] || 0),
          recuperacion: parseInt(normalizedRow['Recuperacion'] || normalizedRow['recuperacion'] || 0),
          defunciones: parseInt(normalizedRow['Defunciones'] || normalizedRow['defunciones'] || 0)
        };

        regionData[region].push(monthData);
      }
    });

    // Ordenar por mes
    Object.keys(regionData).forEach(region => {
      regionData[region].sort((a, b) => {
        return meses.indexOf(a.mes) - meses.indexOf(b.mes);
      });
    });

    return regionData;
  };

  useEffect(() => {
    let interval;
    if (isAnimating && currentMonth < 11) {
      interval = setInterval(() => {
        setCurrentMonth(prev => prev + 1);
      }, 800);
    } else if (currentMonth >= 11) {
      setIsAnimating(false);
    }
    return () => clearInterval(interval);
  }, [isAnimating, currentMonth]);

  const handleAnimate = () => {
    if (!csvData[selectedYear] || !csvData[selectedYear][selectedRegion]) {
      setError('Por favor carga el archivo CSV del a√±o seleccionado primero');
      return;
    }
    setCurrentMonth(0);
    setIsAnimating(true);
  };

  const getCurrentData = () => {
    if (!csvData[selectedYear] || !csvData[selectedYear][selectedRegion]) {
      return [];
    }

    const regionData = csvData[selectedYear][selectedRegion];
    return isAnimating ? regionData.slice(0, currentMonth + 1) : regionData;
  };

  const currentMetric = metrics.find(m => m.value === selectedMetric);
  const hasData = csvData[selectedYear] && csvData[selectedYear][selectedRegion] && csvData[selectedYear][selectedRegion].length > 0;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-green-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8 animate-fade-in">
          <h1 className="text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-green-600 mb-2">
            COVID-19 Oaxaca
          </h1>
          <p className="text-gray-600 text-lg">Simulador Interactivo de Datos Epidemiol√≥gicos</p>
        </div>

        {/* File Upload Section */}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-8 border-2 border-purple-100">
          <h3 className="text-xl font-bold text-gray-800 mb-4 flex items-center gap-2">
            <Upload size={24} className="text-purple-600" />
            Estado de Archivos CSV
          </h3>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {years.map(year => (
              <div key={year} className="space-y-2">
                <label className="block text-sm font-semibold text-gray-700">
                  A√±o {year}
                </label>
                <div className="relative">
                  {filesUploaded[year] ? (
                    <div className="w-full px-4 py-3 bg-gradient-to-r from-green-500 to-emerald-500 text-white rounded-lg font-semibold text-center shadow-md flex items-center justify-center gap-2">
                      <span>‚úì Cargado</span>
                    </div>
                  ) : (
                    <>
                      <input
                        type="file"
                        accept=".csv"
                        onChange={(e) => handleFileUpload(e, year)}
                        className="hidden"
                        id={`file-${year}`}
                      />
                      <label
                        htmlFor={`file-${year}`}
                        className="block w-full px-4 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg font-semibold cursor-pointer hover:from-purple-600 hover:to-pink-600 transition-all transform hover:scale-105 text-center shadow-md"
                      >
                        Cargar Manualmente
                      </label>
                    </>
                  )}
                </div>
                {filesUploaded[year] && (
                  <p className="text-xs text-green-600 truncate" title={filesUploaded[year]}>
                    üìÑ {filesUploaded[year]}
                  </p>
                )}
              </div>
            ))}
          </div>

          {loading && (
            <div className="mt-4 flex items-center justify-center gap-2 text-blue-600">
              <div className="w-4 h-4 border-2 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
              <span className="font-semibold">Cargando archivos CSV...</span>
            </div>
          )}

          {error && (
            <div className="mt-4 flex items-center gap-2 p-4 bg-red-50 border-2 border-red-200 rounded-lg">
              <AlertCircle className="text-red-600" size={20} />
              <span className="text-red-600 font-semibold">{error}</span>
            </div>
          )}

          <div className="mt-4 p-4 bg-blue-50 border-2 border-blue-200 rounded-lg">
            <p className="text-sm text-blue-800 font-semibold mb-2">üìã Archivos buscados autom√°ticamente:</p>
            <ul className="text-xs text-blue-700 space-y-1">
              {years.map(year => (
                <li key={year}>
                  <code className="bg-white px-2 py-0.5 rounded">covid_oaxaca_{year}_mensual.csv</code>
                </li>
              ))}
            </ul>
            <p className="text-xs text-blue-600 mt-2">
              üí° Coloca los archivos CSV en la carpeta <code className="bg-white px-1 rounded">public/data/</code>
            </p>
          </div>
        </div>

        {/* Controls */}
        <div className="bg-white rounded-2xl shadow-xl p-6 mb-8 border-2 border-blue-100">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700 flex items-center gap-2">
                üìÖ A√±o
              </label>
              <select
                value={selectedYear}
                onChange={(e) => setSelectedYear(e.target.value)}
                className="w-full px-4 py-2 rounded-lg border-2 border-blue-200 focus:border-blue-500 focus:outline-none transition-colors"
              >
                {years.map(year => (
                  <option key={year} value={year}>
                    {year} {filesUploaded[year] ? '‚úì' : '‚óã'}
                  </option>
                ))}
              </select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700 flex items-center gap-2">
                üìç Regi√≥n
              </label>
              <select
                value={selectedRegion}
                onChange={(e) => setSelectedRegion(e.target.value)}
                className="w-full px-4 py-2 rounded-lg border-2 border-blue-200 focus:border-blue-500 focus:outline-none transition-colors"
              >
                {regions.map(region => (
                  <option key={region} value={region}>{region}</option>
                ))}
              </select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-semibold text-gray-700 flex items-center gap-2">
                üìä M√©trica
              </label>
              <select
                value={selectedMetric}
                onChange={(e) => setSelectedMetric(e.target.value)}
                className="w-full px-4 py-2 rounded-lg border-2 border-blue-200 focus:border-blue-500 focus:outline-none transition-colors"
              >
                {metrics.map(metric => (
                  <option key={metric.value} value={metric.value}>{metric.label}</option>
                ))}
              </select>
            </div>

            <div className="flex items-end">
              <button
                onClick={handleAnimate}
                disabled={isAnimating || !hasData}
                className="w-full px-4 py-2 bg-gradient-to-r from-blue-500 to-green-500 text-white rounded-lg font-semibold hover:from-blue-600 hover:to-green-600 transition-all transform hover:scale-105 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2 shadow-lg"
              >
                <Play size={18} />
                {isAnimating ? 'Animando...' : 'Animar'}
              </button>
            </div>
          </div>

          <button
            onClick={() => setShowMap(!showMap)}
            className="mt-2 px-4 py-2 bg-blue-100 text-blue-700 rounded-lg font-semibold hover:bg-blue-200 transition-colors flex items-center gap-2"
          >
            <Map size={18} />
            {showMap ? 'Ocultar Mapa' : 'Mostrar Mapa'}
          </button>
        </div>

        {/* Map */}
        {showMap && (
          <div className="bg-white rounded-2xl shadow-xl p-8 mb-8 border-2 border-green-100">
            <h3 className="text-2xl font-bold text-gray-800 mb-4 text-center">Mapa de Oaxaca</h3>
            <div className="flex justify-center">
              <svg width="400" height="300" className="transition-all duration-500">
                {/* Oaxaca outline */}
                <path
                  d="M 50 150 L 100 80 L 180 60 L 250 70 L 320 100 L 350 150 L 320 200 L 280 230 L 200 240 L 120 220 L 80 180 Z"
                  fill="#e0f2fe"
                  stroke="#0284c7"
                  strokeWidth="3"
                  className="animate-pulse"
                />

                {/* Regions */}
                {regions.map((region) => {
                  const coords = regionCoordinates[region];
                  const isSelected = region === selectedRegion;
                  const hasRegionData = csvData[selectedYear] && csvData[selectedYear][region] && csvData[selectedYear][region].length > 0;

                  return (
                    <g key={region}>
                      <circle
                        cx={coords.x}
                        cy={coords.y}
                        r={isSelected ? 16 : 12}
                        fill={hasRegionData ? (isSelected ? '#10b981' : '#3b82f6') : '#9ca3af'}
                        stroke="white"
                        strokeWidth="2"
                        className="cursor-pointer transition-all duration-300 hover:scale-125"
                        onClick={() => setSelectedRegion(region)}
                      />
                      <text
                        x={coords.x}
                        y={coords.y + 30}
                        textAnchor="middle"
                        className="text-xs font-semibold fill-gray-700 pointer-events-none"
                        style={{ fontSize: isSelected ? '11px' : '9px' }}
                      >
                        {region.split(' ')[0]}
                      </text>
                    </g>
                  );
                })}
              </svg>
            </div>
          </div>
        )}

        {/* Chart */}
        <div className="bg-white rounded-2xl shadow-xl p-8 border-2 border-green-100">
          <div className="flex justify-between items-center mb-6">
            <h3 className="text-2xl font-bold text-gray-800">
              {currentMetric.label} - {selectedRegion}
            </h3>
            <div className="flex items-center gap-2 px-4 py-2 rounded-lg" style={{ backgroundColor: currentMetric.color + '20' }}>
              <div className="w-3 h-3 rounded-full" style={{ backgroundColor: currentMetric.color }}></div>
              <span className="text-sm font-semibold" style={{ color: currentMetric.color }}>
                A√±o {selectedYear}
              </span>
            </div>
          </div>

          {hasData ? (
            <>
              <ResponsiveContainer width="100%" height={400}>
                <LineChart data={getCurrentData()}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#e5e7eb" />
                  <XAxis 
                    dataKey="mes" 
                    stroke="#6b7280"
                    style={{ fontSize: '12px', fontWeight: '600' }}
                  />
                  <YAxis 
                    stroke="#6b7280"
                    style={{ fontSize: '12px', fontWeight: '600' }}
                  />
                  <Tooltip 
                    contentStyle={{ 
                      backgroundColor: 'white', 
                      border: '2px solid ' + currentMetric.color,
                      borderRadius: '12px',
                      fontWeight: '600'
                    }}
                  />
                  <Legend wrapperStyle={{ fontWeight: '600' }} />
                  <Line 
                    type="monotone" 
                    dataKey={selectedMetric}
                    stroke={currentMetric.color}
                    strokeWidth={3}
                    dot={{ fill: currentMetric.color, r: 5 }}
                    activeDot={{ r: 8 }}
                    animationDuration={800}
                    name={currentMetric.label}
                  />
                </LineChart>
              </ResponsiveContainer>

              {isAnimating && (
                <div className="mt-4 text-center">
                  <div className="inline-flex items-center gap-2 px-4 py-2 bg-blue-100 rounded-lg">
                    <div className="w-2 h-2 bg-blue-600 rounded-full animate-pulse"></div>
                    <span className="text-sm font-semibold text-blue-700">
                      Mes {currentMonth + 1} de 12
                    </span>
                  </div>
                </div>
              )}
            </>
          ) : (
            <div className="h-96 flex items-center justify-center">
              <div className="text-center">
                <Upload className="mx-auto mb-4 text-gray-400" size={64} />
                <p className="text-xl font-semibold text-gray-600 mb-2">
                  No hay datos disponibles
                </p>
                <p className="text-gray-500">
                  Coloca el archivo <code className="bg-gray-100 px-2 py-1 rounded">covid_oaxaca_{selectedYear}_mensual.csv</code> en <code className="bg-gray-100 px-2 py-1 rounded">public/data/</code>
                </p>
              </div>
            </div>
          )}
        </div>

        {/* Footer */}
        <div className="mt-8 text-center text-gray-600 text-sm">
          <p>Simulador COVID-19 Oaxaca {selectedYear} ‚Ä¢ Coloca los archivos CSV en public/data/</p>
        </div>
      </div>

      <style jsx>{`
        @keyframes fade-in {
          from { opacity: 0; transform: translateY(-20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        .animate-fade-in {
          animation: fade-in 0.6s ease-out;
        }
      `}</style>
    </div>
  );
};

export default OaxacaCovidSimulator;
