import React, { useState, useEffect, useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Play, Upload, Map, AlertCircle } from 'lucide-react';
import Papa from 'papaparse';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import './App.css';

// ícono personalizado para Leaflet
const regionIcon = new L.Icon({
  iconUrl: 'https://cdn-icons-png.flaticon.com/512/252/252025.png',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
});

const OaxacaCovidSimulator = () => {
  const [selectedYear, setSelectedYear] = useState('2020');
  const [selectedRegion, setSelectedRegion] = useState('Valles Centrales');
  const [selectedMetric, setSelectedMetric] = useState('confirmados');
  const [isAnimating, setIsAnimating] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(0);
  const [showMap, setShowMap] = useState(true);
  const [csvData, setCsvData] = useState({});
  const [filesUploaded, setFilesUploaded] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Prediction UI state
  const [showPrediction, setShowPrediction] = useState(true);
  const [predictionMode, setPredictionMode] = useState('base');

  const regions = [
    'Valles Centrales', 'Istmo', 'Costa', 'Mixteca',
    'Sierra Norte', 'Sierra Sur', 'Cañada', 'Papaloapan'
  ];

  const years = ['2020', '2021', '2022', '2023'];
  const metrics = [
    { value: 'confirmados', label: 'Casos Confirmados', color: '#ef4444' },
    { value: 'sospechosos', label: 'Casos Sospechosos', color: '#f59e0b' },
    { value: 'recuperacion', label: 'Recuperaciones', color: '#10b981' },
    { value: 'defunciones', label: 'Defunciones', color: '#6b7280' }
  ];

  // Coordenadas reales de las 8 regiones de Oaxaca (aproximadas)
  const regionCoordinates = {
    'Valles Centrales': [17.06, -96.72],
    'Istmo': [16.56, -94.93],
    'Costa': [16.02, -97.03],
    'Mixteca': [17.81, -97.57],
    'Sierra Norte': [17.45, -96.40],
    'Sierra Sur': [16.22, -96.72],
    'Cañada': [18.13, -96.83],
    'Papaloapan': [18.08, -96.10]
  };

  const meses = [
    'Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
    'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
  ];

  // ----------- CARGA AUTOMÁTICA DE CSV -----------
  useEffect(() => {
    const loadAllCSVs = async () => {
      setLoading(true);
      const allData = {};
      const filesLoaded = {};

      for (const year of years) {
        try {
          const response = await fetch(`/data/covid_oaxaca_${year}_mensual.csv`);
          if (!response.ok) continue;
          const data = await response.text();
          Papa.parse(data, {
            header: true,
            skipEmptyLines: true,
            dynamicTyping: true,
            complete: (results) => {
              allData[year] = processCSVData(results.data);
              filesLoaded[year] = true;
            }
          });
        } catch (err) { }
      }

      setCsvData(allData);
      setFilesUploaded(filesLoaded);
      setLoading(false);
    };

    loadAllCSVs();
  }, []);

  // ----------- PROCESAMIENTO DEL CSV -----------
  const processCSVData = (data) => {
    const regionData = {};
    regions.forEach(r => { regionData[r] = []; });

    data.forEach(row => {
      const clean = {};
      Object.keys(row).forEach(k => { clean[k.trim()] = row[k]; });

      const region = clean['Region'] || clean['region'];
      const mes = clean['Mes'] || clean['mes'];

      if (region && mes && regions.includes(region)) {
        const registro = {
          mes,
          confirmados: parseInt(clean['Casos Confirmados'] || 0),
          sospechosos: parseInt(clean['Casos Sospechosos'] || 0),
          recuperacion: parseInt(clean['Recuperaciones'] || 0),
          defunciones: parseInt(clean['Defunciones'] || 0)
        };
        regionData[region].push(registro);
      }
    });

    Object.keys(regionData).forEach(r => {
      regionData[r].sort((a, b) => meses.indexOf(a.mes) - meses.indexOf(b.mes));
    });

    return regionData;
  };

  // ----------- ANIMACIÓN -----------
  useEffect(() => {
    let timer;
    if (isAnimating && currentMonth < 11) {
      timer = setInterval(() => setCurrentMonth(prev => prev + 1), 800);
    } else if (currentMonth >= 11) setIsAnimating(false);
    return () => clearInterval(timer);
  }, [isAnimating, currentMonth]);

  const handleAnimate = () => {
    if (!csvData[selectedYear]?.[selectedRegion]) {
      setError('Primero carga el CSV de ese año.');
      return;
    }
    setCurrentMonth(0);
    setIsAnimating(true);
  };

  // ----------- PREDICCIÓN 2024 -----------
  const linearRegression = (xs, ys) => {
    const n = xs.length;
    if (n === 0) return { m: 0, b: 0 };
    const sumX = xs.reduce((a, b) => a + b, 0);
    const sumY = ys.reduce((a, b) => a + b, 0);
    const sumXY = xs.reduce((a, b, i) => a + b * ys[i], 0);
    const sumXX = xs.reduce((a, b) => a + b * b, 0);
    const denom = n * sumXX - sumX ** 2;
    if (!denom) return { m: 0, b: sumY / n };
    const m = (n * sumXY - sumX * sumY) / denom;
    const b = (sumY - m * sumX) / n;
    return { m, b };
  };

  const predict2024ForRegionMetric = (region, metric) => {
    const predictions = new Array(12).fill(0);
    const yearsNum = years.map(y => parseInt(y));
    for (let m = 0; m < 12; m++) {
      const xs = [], ys = [];
      years.forEach((y, i) => {
        const v = csvData[y]?.[region]?.[m]?.[metric];
        if (v) { xs.push(yearsNum[i]); ys.push(v); }
      });
      if (ys.length <= 1) {
        predictions[m] = ys[0] || 0;
        continue;
      }
      const { m: slope, b } = linearRegression(xs, ys);
      predictions[m] = Math.max(0, Math.round(slope * 2024 + b));
    }
    return predictions;
  };

  // ----------- DATOS PARA EL GRÁFICO -----------
  const chartData = useMemo(() => {
    const hist = csvData[selectedYear]?.[selectedRegion] || [];
    const predBase = predict2024ForRegionMetric(selectedRegion, selectedMetric);
    const predOpt = predBase.map(v => Math.round(v * 0.85));
    const predPes = predBase.map(v => Math.round(v * 1.25));
    return meses.map((mes, i) => ({
      mes,
      historial: hist[i]?.[selectedMetric] ?? null,
      pred_base: predBase[i],
      pred_optimista: predOpt[i],
      pred_pesimista: predPes[i]
    }));
  }, [csvData, selectedYear, selectedRegion, selectedMetric]);

  const displayData = isAnimating
    ? chartData.map((d, i) => ({ ...d, historial: i <= currentMonth ? d.historial : null }))
    : chartData;

  return (
    <div className="app-container">
      <header>
        <h1>COVID-19 Oaxaca</h1>
        <p>Simulador Interactivo y Predicción 2024</p>
      </header>

      <section className="card">
        <h2><Upload size={18}/> Archivos CSV</h2>
        <div className="year-row">
          {years.map(y => (
            <div key={y} className={`year-tile ${filesUploaded[y] ? 'year-yes' : 'year-no'}`}>
              <div className="year-label">{y}</div>
              <input type="file" accept=".csv" onChange={(e) => {
                const f = e.target.files[0];
                if (!f) return;
                Papa.parse(f, {
                  header: true,
                  complete: (res) => {
                    setCsvData(prev => ({ ...prev, [y]: processCSVData(res.data) }));
                    setFilesUploaded(prev => ({ ...prev, [y]: true }));
                  }
                });
              }} />
            </div>
          ))}
        </div>
      </section>

      {showMap && (
        <section className="card map-section">
          <h3>Mapa Interactivo de Oaxaca</h3>
          <MapContainer center={[17.0, -96.7]} zoom={7} scrollWheelZoom={false} style={{ height: '400px', borderRadius: '16px' }}>
            <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
            {Object.entries(regionCoordinates).map(([region, coords]) => (
              <Marker key={region} position={coords} icon={regionIcon} eventHandlers={{
                click: () => setSelectedRegion(region)
              }}>
                <Popup>
                  <strong>{region}</strong><br />
                  {csvData[selectedYear]?.[region]?.length
                    ? 'Datos cargados ✓'
                    : 'Sin datos'}
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </section>
      )}

      {/* GRAFICA */}
      <section className="card chart-section">
        <h3>{metrics.find(m => m.value === selectedMetric)?.label} — {selectedRegion}</h3>
        <ResponsiveContainer width="100%" height={380}>
          <LineChart data={displayData}>
            <CartesianGrid strokeDasharray="4 4" />
            <XAxis dataKey="mes" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="historial" stroke="#3b82f6" strokeWidth={3} />
            {showPrediction && (
              <>
                {(predictionMode === 'base' || predictionMode === 'todos') &&
                  <Line type="monotone" dataKey="pred_base" stroke="#f97316" strokeDasharray="6 6" />}
                {(predictionMode === 'optimista' || predictionMode === 'todos') &&
                  <Line type="monotone" dataKey="pred_optimista" stroke="#0ea5e9" strokeDasharray="3 6" />}
                {(predictionMode === 'pesimista' || predictionMode === 'todos') &&
                  <Line type="monotone" dataKey="pred_pesimista" stroke="#dc2626" strokeDasharray="2 6" />}
              </>
            )}
          </LineChart>
        </ResponsiveContainer>
      </section>
    </div>
  );
};

export default OaxacaCovidSimulator;
