import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Play, Upload, Map, AlertCircle } from 'lucide-react';
import Papa from 'papaparse';
import './App.css';

const OaxacaCovidSimulator = () => {
  const [selectedYear, setSelectedYear] = useState('2020');
  const [selectedRegion, setSelectedRegion] = useState('Valles Centrales');
  const [selectedMetric, setSelectedMetric] = useState('confirmados');
  const [isAnimating, setIsAnimating] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(0);
  const [showMap, setShowMap] = useState(true);
  const [csvData, setCsvData] = useState({});
  const [filesUploaded, setFilesUploaded] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const regions = ['Valles Centrales', 'Istmo', 'Costa', 'Mixteca', 'Sierra Norte', 'Sierra Sur', 'Cañada', 'Papaloapan'];
  const years = ['2020', '2021', '2022', '2023'];
  const metrics = [
    { value: 'confirmados', label: 'Casos Confirmados', color: '#ef4444', key: 'Casos Confirmados' },
    { value: 'sospechosos', label: 'Casos Sospechosos', color: '#f59e0b', key: 'Casos Sospechosos' },
    { value: 'recuperacion', label: 'Recuperaciones', color: '#10b981', key: 'Recuperacion' },
    { value: 'defunciones', label: 'Defunciones', color: '#6b7280', key: 'Defunciones' }
  ];

  const regionCoordinates = {
    'Valles Centrales': { x: 180, y: 140 },
    'Istmo': { x: 280, y: 180 },
    'Costa': { x: 100, y: 200 },
    'Mixteca': { x: 120, y: 80 },
    'Sierra Norte': { x: 200, y: 60 },
    'Sierra Sur': { x: 160, y: 180 },
    'Cañada': { x: 220, y: 100 },
    'Papaloapan': { x: 260, y: 80 }
  };

  const meses = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 
                 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];

  useEffect(() => {
    const loadAllCSVs = async () => {
      setLoading(true);
      const allData = {};
      const filesLoaded = {};

      for (const year of years) {
        try {
          const filename = `/data/covid_oaxaca_${year}_mensual.csv`;
          const response = await fetch(filename);
          const data = await response.text();

          Papa.parse(data, {
            header: true,
            skipEmptyLines: true,
            dynamicTyping: true,
            complete: (results) => {
              const processedData = processCSVData(results.data);
              allData[year] = processedData;
              filesLoaded[year] = `covid_oaxaca_${year}_mensual.csv`;
            }
          });
        } catch (err) {
          console.log(`No se encontró el archivo para ${year}: ${err.message}`);
        }
      }

      setCsvData(allData);
      setFilesUploaded(filesLoaded);
      setLoading(false);
    };

    loadAllCSVs();
  }, []);

  const handleFileUpload = (event, year) => {
    const file = event.target.files[0];
    if (!file) return;
    setLoading(true);
    setError('');

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      dynamicTyping: true,
      complete: (results) => {
        try {
          const processedData = processCSVData(results.data);
          setCsvData(prev => ({ ...prev, [year]: processedData }));
          setFilesUploaded(prev => ({ ...prev, [year]: file.name }));
          setLoading(false);
        } catch (err) {
          setError(`Error procesando ${year}: ${err.message}`);
          setLoading(false);
        }
      },
      error: (err) => {
        setError(`Error leyendo archivo: ${err.message}`);
        setLoading(false);
      }
    });
  };

  const processCSVData = (data) => {
    const regionData = {};
    regions.forEach(region => { regionData[region] = []; });

    data.forEach(row => {
      const normalizedRow = {};
      Object.keys(row).forEach(key => { normalizedRow[key.trim()] = row[key]; });

      const region = normalizedRow['Region'] || normalizedRow['region'];
      const mes = normalizedRow['Mes'] || normalizedRow['mes'];

      if (region && mes && regions.includes(region)) {
        const monthData = {
          mes: mes,
          confirmados: parseInt(normalizedRow['Casos Confirmados'] || 0),
          sospechosos: parseInt(normalizedRow['Casos Sospechosos'] || 0),
          recuperacion: parseInt(normalizedRow['Recuperacion'] || 0),
          defunciones: parseInt(normalizedRow['Defunciones'] || 0)
        };
        regionData[region].push(monthData);
      }
    });

    Object.keys(regionData).forEach(region => {
      regionData[region].sort((a, b) => meses.indexOf(a.mes) - meses.indexOf(b.mes));
    });
    return regionData;
  };

  useEffect(() => {
    let interval;
    if (isAnimating && currentMonth < 11) {
      interval = setInterval(() => setCurrentMonth(prev => prev + 1), 800);
    } else if (currentMonth >= 11) {
      setIsAnimating(false);
    }
    return () => clearInterval(interval);
  }, [isAnimating, currentMonth]);

  const handleAnimate = () => {
    if (!csvData[selectedYear] || !csvData[selectedYear][selectedRegion]) {
      setError('Por favor carga el archivo CSV del año seleccionado primero');
      return;
    }
    setCurrentMonth(0);
    setIsAnimating(true);
  };

  const getCurrentData = () => {
    if (!csvData[selectedYear] || !csvData[selectedYear][selectedRegion]) return [];
    const regionData = csvData[selectedYear][selectedRegion];
    return isAnimating ? regionData.slice(0, currentMonth + 1) : regionData;
  };

  const currentMetric = metrics.find(m => m.value === selectedMetric);
  const hasData = csvData[selectedYear] && csvData[selectedYear][selectedRegion]?.length > 0;

  return (
    <div className="app-container">
      <header>
        <h1>COVID-19 Oaxaca</h1>
        <p>Simulador Interactivo de Datos Epidemiológicos</p>
      </header>

      <section className="card">
        <h2><Upload size={18}/> Archivos CSV C</h2>
        <div className="file-grid">
          {years.map(year => (
            <div key={year} className="file-item">
              <label>Año {year}</label>
              {filesUploaded[year] ? (
                <div className="file-loaded">✓ {filesUploaded[year]}</div>
              ) : (
                <>
                  <input type="file" accept=".csv" id={`file-${year}`} onChange={(e) => handleFileUpload(e, year)} />
                  <label htmlFor={`file-${year}`} className="btn">Cargar CSV</label>
                </>
              )}
            </div>
          ))}
        </div>
        {loading && <div className="loader"></div>}
        {error && <p className="error"><AlertCircle size={16}/> {error}</p>}
      </section>

      <section className="controls card">
        <div className="control-group">
          <label>Año</label>
          <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
            {years.map(y => <option key={y}>{y}</option>)}
          </select>
        </div>

        <div className="control-group">
          <label>Región</label>
          <select value={selectedRegion} onChange={(e) => setSelectedRegion(e.target.value)}>
            {regions.map(r => <option key={r}>{r}</option>)}
          </select>
        </div>

        <div className="control-group">
          <label>Métrica</label>
          <select value={selectedMetric} onChange={(e) => setSelectedMetric(e.target.value)}>
            {metrics.map(m => <option key={m.value} value={m.value}>{m.label}</option>)}
          </select>
        </div>

        <button onClick={handleAnimate} disabled={isAnimating || !hasData}><Play size={14}/> Animar</button>
        <button onClick={() => setShowMap(!showMap)}><Map size={14}/> {showMap ? 'Ocultar Mapa' : 'Mostrar Mapa'}</button>
      </section>

      {showMap && (
        <section className="card map-section">
          <h3>Mapa de Oaxaca</h3>
          <svg width="400" height="300">
            <path d="M 50 150 L 100 80 L 180 60 L 250 70 L 320 100 L 350 150 L 320 200 L 280 230 L 200 240 L 120 220 L 80 180 Z"
              fill="#eef6ff" stroke="#3b82f6" strokeWidth="2" />
            {regions.map(region => {
              const coords = regionCoordinates[region];
              const isSelected = region === selectedRegion;
              const hasRegionData = csvData[selectedYear]?.[region]?.length > 0;
              return (
                <g key={region}>
                  <circle cx={coords.x} cy={coords.y} r={isSelected ? 14 : 10}
                    fill={hasRegionData ? (isSelected ? '#10b981' : '#3b82f6') : '#aaa'}
                    stroke="white" strokeWidth="2" onClick={() => setSelectedRegion(region)} />
                  <text x={coords.x} y={coords.y + 25} textAnchor="middle" className="region-label">{region.split(' ')[0]}</text>
                </g>
              );
            })}
          </svg>
        </section>
      )}

      <section className="card chart-section">
        <h3>{currentMetric.label} - {selectedRegion}</h3>
        {hasData ? (
          <ResponsiveContainer width="100%" height={350}>
            <LineChart data={getCurrentData()}>
              <CartesianGrid strokeDasharray="3 3" stroke="#ccc" />
              <XAxis dataKey="mes" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey={selectedMetric} stroke={currentMetric.color} strokeWidth={3} />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <p className="no-data">No hay datos disponibles para este año o región.</p>
        )}
      </section>
    </div>
  );
};

export default OaxacaCovidSimulator;
