import React, { useState, useEffect } from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  ResponsiveContainer,
  BarChart,
  Bar,
  CartesianGrid,
} from "recharts";
import Papa from "papaparse";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

// üü¢ Lista de archivos CSV por a√±o
const csvFiles = {
  2020: "/data/covid_oaxaca_2020_mensual.csv",
  2021: "/data/covid_oaxaca_2021_mensual.csv",
  2022: "/data/covid_oaxaca_2022_mensual.csv",
  2023: "/data/covid_oaxaca_2023_mensual.csv",
};

// üü¢ Lista de regiones
const regions = [
  "Valles Centrales",
  "Sierra Norte",
  "Sierra Sur",
  "Ca√±ada",
  "Mixteca",
  "Costa",
  "Istmo",
  "Papaloapan",
];

// üü¢ Variables a comparar
const metrics = [
  "casos_confirmados",
  "defunciones",
  "casos_sospechosos",
  "casos_negativos",
];

// üü¢ Funci√≥n para predecir con regresi√≥n lineal
const predictForRegionMetric = (region, metric, year, availableYears) => {
  const x = availableYears.map((y) => parseInt(y));
  const y = availableYears.map((y) => {
    const regionData = csvData[y]?.[region] || [];
    const total =
      regionData.reduce((sum, row) => sum + (parseFloat(row[metric]) || 0), 0) /
      regionData.length;
    return total;
  });

  const n = x.length;
  const meanX = x.reduce((a, b) => a + b, 0) / n;
  const meanY = y.reduce((a, b) => a + b, 0) / n;
  const b1 =
    x.reduce((sum, xi, i) => sum + (xi - meanX) * (y[i] - meanY), 0) /
    x.reduce((sum, xi) => sum + (xi - meanX) ** 2, 0);
  const b0 = meanY - b1 * meanX;

  const predicted = b0 + b1 * year;
  return Array(12).fill(predicted / 12);
};

// üü¢ Datos CSV cargados globalmente
let csvData = {};

// üü¢ Nueva funci√≥n corregida
const predict2023ForRegionMetric = (region, metric) => {
  // ‚úÖ Si hay datos reales de 2023 cargados, √∫salos directamente
  if (csvData["2023"] && csvData["2023"][region]) {
    return csvData["2023"][region].map((m) => m[metric] || 0);
  }

  // üîÑ Si no hay datos, predecir usando 2020‚Äì2022
  const availableYears = Object.keys(csvData).filter(
    (y) => parseInt(y) <= 2022
  );
  return predictForRegionMetric(region, metric, 2023, availableYears);
};

// üü¢ Funci√≥n principal
export default function CovidOaxacaDashboard() {
  const [year, setYear] = useState("2023");
  const [selectedRegion, setSelectedRegion] = useState("Valles Centrales");
  const [dataReady, setDataReady] = useState(false);

  useEffect(() => {
    const loadAllData = async () => {
      for (const [y, path] of Object.entries(csvFiles)) {
        const response = await fetch(path);
        const text = await response.text();
        const parsed = Papa.parse(text, { header: true }).data;

        csvData[y] = {};

        parsed.forEach((row) => {
          const region = row.region;
          if (!csvData[y][region]) csvData[y][region] = [];
          csvData[y][region].push({
            mes: row.mes,
            casos_confirmados: parseFloat(row.casos_confirmados) || 0,
            defunciones: parseFloat(row.defunciones) || 0,
            casos_sospechosos: parseFloat(row.casos_sospechosos) || 0,
            casos_negativos: parseFloat(row.casos_negativos) || 0,
          });
        });
      }
      setDataReady(true);
    };

    loadAllData();
  }, []);

  if (!dataReady) return <div className="p-8 text-center">Cargando datos...</div>;

  // üßÆ Generar datos para gr√°ficos
  const months = [
    "Ene",
    "Feb",
    "Mar",
    "Abr",
    "May",
    "Jun",
    "Jul",
    "Ago",
    "Sep",
    "Oct",
    "Nov",
    "Dic",
  ];

  const chartData = months.map((mes, i) => {
    const result = { mes };

    metrics.forEach((metric) => {
      const regionData =
        csvData[year]?.[selectedRegion] ||
        Array(12).fill({ [metric]: 0 });

      result[metric] =
        regionData[i]?.[metric] ||
        predict2023ForRegionMetric(selectedRegion, metric)[i];
    });

    return result;
  });

  // üß© Datos para comparaci√≥n anual (gr√°fico de barras)
  const comparisonData = metrics.map((metric) => {
    const totalByYear = {};

    Object.keys(csvData).forEach((y) => {
      const regionData = csvData[y]?.[selectedRegion];
      if (regionData) {
        totalByYear[y] = regionData.reduce(
          (sum, m) => sum + (m[metric] || 0),
          0
        );
      } else if (y === "2023") {
        // Si no hay datos reales de 2023, usa predicci√≥n
        totalByYear["2023"] = predict2023ForRegionMetric(
          selectedRegion,
          metric
        ).reduce((a, b) => a + b, 0);
      }
    });

    return {
      metric,
      ...totalByYear,
    };
  });

  return (
    <div className="max-w-7xl mx-auto p-6 font-sans bg-gradient-to-br from-sky-50 to-amber-50 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-6 text-gray-800">
        COVID-19 Oaxaca ‚Äî An√°lisis y Predicci√≥n por Regi√≥n
      </h1>

      {/* Controles */}
      <div className="flex justify-center gap-4 mb-6">
        <select
          value={selectedRegion}
          onChange={(e) => setSelectedRegion(e.target.value)}
          className="border p-2 rounded"
        >
          {regions.map((r) => (
            <option key={r}>{r}</option>
          ))}
        </select>

        <select
          value={year}
          onChange={(e) => setYear(e.target.value)}
          className="border p-2 rounded"
        >
          {Object.keys(csvFiles).map((y) => (
            <option key={y}>{y}</option>
          ))}
        </select>
      </div>

      {/* Gr√°ficos por m√©trica */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {metrics.map((metric) => (
          <Card key={metric}>
            <CardContent>
              <h2 className="text-lg font-semibold mb-2 text-gray-700 capitalize">
                {metric.replace("_", " ")}
              </h2>
              <ResponsiveContainer width="100%" height={250}>
                <LineChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="mes" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  <Line
                    type="monotone"
                    dataKey={metric}
                    stroke="#007BFF"
                    strokeWidth={2}
                    dot={false}
                  />
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Comparaci√≥n anual */}
      <Card className="mt-10">
        <CardContent>
          <h2 className="text-lg font-semibold mb-4 text-gray-700">
            Comparaci√≥n anual por m√©trica ({selectedRegion})
          </h2>
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={comparisonData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="metric" />
              <YAxis />
              <Tooltip />
              <Legend />
              {Object.keys(csvFiles).map((y, i) => (
                <Bar key={y} dataKey={y} fill={`hsl(${i * 60}, 70%, 50%)`} />
              ))}
            </BarChart>
          </ResponsiveContainer>
        </CardContent>
      </Card>
    </div>
  );
}
