import React, { useState, useEffect, useMemo } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Play, Upload, Map, AlertCircle } from 'lucide-react';
import Papa from 'papaparse';
import './App.css';

const OaxacaCovidSimulator = () => {
  const [selectedYear, setSelectedYear] = useState('2020');
  const [selectedRegion, setSelectedRegion] = useState('Valles Centrales');
  const [selectedMetric, setSelectedMetric] = useState('confirmados');
  const [isAnimating, setIsAnimating] = useState(false);
  const [currentMonth, setCurrentMonth] = useState(0);
  const [showMap, setShowMap] = useState(true);
  const [csvData, setCsvData] = useState({});
  const [filesUploaded, setFilesUploaded] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Prediction UI state
  const [showPrediction, setShowPrediction] = useState(true);
  const [predictionMode, setPredictionMode] = useState('base'); // 'base' | 'optimista' | 'pesimista' | 'todos'

  const regions = ['Valles Centrales', 'Istmo', 'Costa', 'Mixteca', 'Sierra Norte', 'Sierra Sur', 'Ca√±ada', 'Papaloapan'];
  const years = ['2020', '2021', '2022', '2023'];
  const metrics = [
    { value: 'confirmados', label: 'Casos Confirmados', color: '#ef4444' },
    { value: 'sospechosos', label: 'Casos Sospechosos', color: '#f59e0b' },
    { value: 'recuperacion', label: 'Recuperaciones', color: '#10b981' },
    { value: 'defunciones', label: 'Defunciones', color: '#6b7280' }
  ];

  const regionCoordinates = {
    'Valles Centrales': { x: 180, y: 140 },
    'Istmo': { x: 280, y: 180 },
    'Costa': { x: 100, y: 200 },
    'Mixteca': { x: 120, y: 80 },
    'Sierra Norte': { x: 200, y: 60 },
    'Sierra Sur': { x: 160, y: 180 },
    'Ca√±ada': { x: 220, y: 100 },
    'Papaloapan': { x: 260, y: 80 }
  };

  const meses = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio',
                 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];

  // ---------- CSV Loading (igual que antes) ----------
  useEffect(() => {
    const loadAllCSVs = async () => {
      setLoading(true);
      const allData = {};
      const filesLoaded = {};

      for (const year of years) {
        try {
          const filename = `/data/covid_oaxaca_${year}_mensual.csv`;
          const response = await fetch(filename);
          const data = await response.text();

          Papa.parse(data, {
            header: true,
            skipEmptyLines: true,
            dynamicTyping: true,
            complete: (results) => {
              const processedData = processCSVData(results.data);
              allData[year] = processedData;
              filesLoaded[year] = true; // solo guardamos booleano
            }
          });
        } catch (err) {
          // no encontrado -> ignorar
        }
      }

      setCsvData(allData);
      setFilesUploaded(filesLoaded);
      setLoading(false);
    };

    loadAllCSVs();
  }, []);

  const handleFileUpload = (event, year) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError('');

    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      dynamicTyping: true,
      complete: (results) => {
        try {
          const processedData = processCSVData(results.data);

          setCsvData(prev => ({
            ...prev,
            [year]: processedData
          }));

          setFilesUploaded(prev => ({
            ...prev,
            [year]: true
          }));

          setLoading(false);
        } catch (err) {
          setError(`Error procesando ${year}: ${err.message}`);
          setLoading(false);
        }
      },
      error: (err) => {
        setError(`Error leyendo archivo: ${err.message}`);
        setLoading(false);
      }
    });
  };

  const processCSVData = (data) => {
    const regionData = {};
    regions.forEach(region => { regionData[region] = []; });

    data.forEach(row => {
      const normalizedRow = {};
      Object.keys(row).forEach(key => { normalizedRow[key.trim()] = row[key]; });

      const region = normalizedRow['Region'] || normalizedRow['region'];
      const mes = normalizedRow['Mes'] || normalizedRow['mes'];

      if (region && mes && regions.includes(region)) {
        const monthData = {
          mes: mes,
          confirmados: parseInt(normalizedRow['Casos Confirmados'] || normalizedRow['Casos Confir'] || normalizedRow['confirmados'] || 0),
          sospechosos: parseInt(normalizedRow['Casos Sospechosos'] || normalizedRow['Casos Sospe'] || normalizedRow['sospechosos'] || 0),
          recuperacion: parseInt(normalizedRow['Recuperacion'] || normalizedRow['recuperacion'] || 0),
          defunciones: parseInt(normalizedRow['Defunciones'] || normalizedRow['defunciones'] || 0)
        };

        regionData[region].push(monthData);
      }
    });

    // ordenar por meses
    Object.keys(regionData).forEach(region => {
      regionData[region].sort((a, b) => {
        return meses.indexOf(a.mes) - meses.indexOf(b.mes);
      });
    });

    return regionData;
  };

  useEffect(() => {
    let interval;
    if (isAnimating && currentMonth < 11) {
      interval = setInterval(() => {
        setCurrentMonth(prev => prev + 1);
      }, 800);
    } else if (currentMonth >= 11) {
      setIsAnimating(false);
    }
    return () => clearInterval(interval);
  }, [isAnimating, currentMonth]);

  const handleAnimate = () => {
    if (!csvData[selectedYear] || !csvData[selectedYear][selectedRegion]) {
      setError('Por favor carga el archivo CSV del a√±o seleccionado primero');
      return;
    }
    setCurrentMonth(0);
    setIsAnimating(true);
  };

  // ---------- PREDICTION LOGIC ----------
  // Simple linear regression (least squares) given arrays x and y
  const linearRegression = (xs, ys) => {
    const n = xs.length;
    if (n === 0) return { m: 0, b: 0 };
    const sumX = xs.reduce((a, b) => a + b, 0);
    const sumY = ys.reduce((a, b) => a + b, 0);
    const sumXY = xs.reduce((a, b, i) => a + b * ys[i], 0);
    const sumXX = xs.reduce((a, b) => a + b * b, 0);

    const denom = (n * sumXX - sumX * sumX);
    if (denom === 0) return { m: 0, b: sumY / n || 0 };

    const m = (n * sumXY - sumX * sumY) / denom;
    const b = (sumY - m * sumX) / n;
    return { m, b };
  };

  // Return predicted 12 months for given region & metric
  const predict2024ForRegionMetric = (region, metric) => {
    // build per-month arrays of values across years
    const predictions = new Array(12).fill(0);
    const yearsNumeric = years.map(y => parseInt(y, 10)); // [2020..2023]
    for (let mi = 0; mi < 12; mi++) {
      const ys = [];
      const xs = [];
      years.forEach((y, idx) => {
        const yearData = csvData[y];
        const val = yearData?.[region]?.[mi]?.[metric];
        if (typeof val === 'number' && !isNaN(val)) {
          xs.push(yearsNumeric[idx]);
          ys.push(val);
        }
      });

      // if no data -> fallback to last available (prefer 2023)
      if (ys.length === 0) {
        const last = csvData['2023']?.[region]?.[mi]?.[metric] ?? 0;
        predictions[mi] = last;
        continue;
      }

      // if only one point -> use that value
      if (ys.length === 1) {
        // small trend: same value
        predictions[mi] = ys[0];
        continue;
      }

      const { m, b } = linearRegression(xs, ys);
      const pred = m * 2024 + b;
      // clamp: no negative counts
      predictions[mi] = Math.max(0, Math.round(pred));
    }

    return predictions;
  };

  // Compute the chart data merging historical and predicted values
  const chartData = useMemo(() => {
    // historical values for selectedYear & selectedRegion
    const hist = (csvData[selectedYear]?.[selectedRegion]) || [];
    const predBase = predict2024ForRegionMetric(selectedRegion, selectedMetric);
    // scenarios
    const predOptimista = predBase.map(v => Math.max(0, Math.round(v * 0.85))); // -15%
    const predPesimista = predBase.map(v => Math.round(v * 1.25)); // +25%

    // Prepare merged data per month
    return meses.map((mes, idx) => {
      const historicalVal = hist[idx]?.[selectedMetric];
      return {
        mes,
        historial: typeof historicalVal === 'number' ? historicalVal : null,
        pred_base: predBase[idx],
        pred_optimista: predOptimista[idx],
        pred_pesimista: predPesimista[idx]
      };
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [csvData, selectedYear, selectedRegion, selectedMetric]); // recalcula cuando cambian

  const currentMetricObj = metrics.find(m => m.value === selectedMetric) || metrics[0];
  const hasData = csvData[selectedYear] && csvData[selectedYear][selectedRegion] && csvData[selectedYear][selectedRegion].length > 0;

  // If animating, we will slice chartData (historial) to currentMonth+1, but predictions should remain visible
  const displayedData = useMemo(() => {
    if (!isAnimating) return chartData;
    // for historial show up to currentMonth, but keep predictions fields
    return chartData.map((d, i) => ({
      ...d,
      historial: i <= currentMonth ? d.historial : null
    }));
  }, [chartData, isAnimating, currentMonth]);

  return (
    <div className="app-container">
      <header>
        <h1>COVID-19 Oaxaca</h1>
        <p>Simulador Interactivo de Datos Epidemiol√≥gicos</p>
      </header>

      {/* Archivo CSV: ahora solo se muestran a√±os */}
      <section className="card">
        <h2><Upload size={18}/> Estado de Archivos CSV</h2>

        <div className="year-row">
          {years.map(year => (
            <div key={year} className={`year-tile ${filesUploaded[year] ? 'year-yes' : 'year-no'}`}>
              <div className="year-label">{year}</div>
              <div className="year-status">{filesUploaded[year] ? '‚úì' : '‚óã'}</div>

              {/* file input (oculto) */}
              <input type="file" id={`file-${year}`} accept=".csv" onChange={(e) => handleFileUpload(e, year)} />
              <label htmlFor={`file-${year}`} className="year-upload">üìÅ</label>
            </div>
          ))}
        </div>

        {loading && <div className="loader" />}

        {error && (
          <div className="error"><AlertCircle size={16}/> {error}</div>
        )}

        <p className="muted">Coloca los archivos CSV en <code>/public/data/</code> o s√∫belos manualmente.</p>
      </section>

      {/* Controls */}
      <section className="controls card">
        <div className="control-group">
          <label>A√±o</label>
          <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
            {years.map(y => <option key={y} value={y}>{y}</option>)}
          </select>
        </div>

        <div className="control-group">
          <label>Regi√≥n</label>
          <select value={selectedRegion} onChange={(e) => setSelectedRegion(e.target.value)}>
            {regions.map(r => <option key={r} value={r}>{r}</option>)}
          </select>
        </div>

        <div className="control-group">
          <label>M√©trica</label>
          <select value={selectedMetric} onChange={(e) => setSelectedMetric(e.target.value)}>
            {metrics.map(m => <option key={m.value} value={m.value}>{m.label}</option>)}
          </select>
        </div>

        <div className="control-actions">
          <button onClick={handleAnimate} disabled={isAnimating || !hasData}><Play size={14}/> Animar</button>
          <button onClick={() => setShowMap(!showMap)}><Map size={14}/> {showMap ? 'Ocultar Mapa' : 'Mostrar Mapa'}</button>
        </div>

        <div className="predict-controls">
          <label className="inline-label">
            <input type="checkbox" checked={showPrediction} onChange={(e) => setShowPrediction(e.target.checked)} />
            Mostrar predicci√≥n 2024
          </label>

          <label>
            Escenario:
            <select value={predictionMode} onChange={(e) => setPredictionMode(e.target.value)}>
              <option value="base">Base</option>
              <option value="optimista">Optimista (-15%)</option>
              <option value="pesimista">Pesimista (+25%)</option>
              <option value="todos">Mostrar todos</option>
            </select>
          </label>
        </div>
      </section>

      {/* Map */}
      {showMap && (
        <section className="card map-section">
          <h3>Mapa de Oaxaca</h3>
          <svg width="520" height="320">
            <path d="M 50 150 L 100 80 L 180 60 L 250 70 L 320 100 L 350 150 L 320 200 L 280 230 L 200 240 L 120 220 L 80 180 Z"
              fill="#eef6ff" stroke="#3b82f6" strokeWidth="2" />
            {regions.map(region => {
              const coords = regionCoordinates[region];
              const isSelected = region === selectedRegion;
              const hasRegionData = csvData[selectedYear]?.[region]?.length > 0;
              return (
                <g key={region}>
                  <circle cx={coords.x} cy={coords.y} r={isSelected ? 14 : 10}
                    fill={hasRegionData ? (isSelected ? '#10b981' : '#3b82f6') : '#aaa'}
                    stroke="#fff" strokeWidth="2" onClick={() => setSelectedRegion(region)}
                    className={hasRegionData ? 'map-dot active' : 'map-dot'} />
                  <text x={coords.x} y={coords.y + 26} textAnchor="middle" className="region-label">{region.split(' ')[0]}</text>
                </g>
              );
            })}
          </svg>
        </section>
      )}

      {/* Chart with prediction */}
      <section className="card chart-section">
        <div className="chart-header">
          <h3>{metrics.find(m => m.value === selectedMetric)?.label} ‚Äî {selectedRegion}</h3>
          <div className="legend-pill" style={{ borderColor: currentMetricObj.color }}>
            <span className="legend-dot" style={{ background: currentMetricObj.color }} />
            A√±o {selectedYear}
          </div>
        </div>

        { (csvData[selectedYear]?.[selectedRegion]?.length > 0) ? (
          <ResponsiveContainer width="100%" height={380}>
            <LineChart data={displayedData}>
              <CartesianGrid strokeDasharray="4 4" stroke="#e6edf6" />
              <XAxis dataKey="mes" />
              <YAxis />
              <Tooltip />
              <Legend />
              {/* Historial */}
              <Line type="monotone" dataKey="historial" name={`Hist√≥rico ${selectedYear}`} stroke={currentMetricObj.color} strokeWidth={3} dot={{ r: 4 }} />
              {/* Predictions */}
              { showPrediction && (predictionMode === 'base' || predictionMode === 'todos') && (
                <Line type="monotone" dataKey="pred_base" name="Predicci√≥n 2024 (Base)" stroke="#fb923c" strokeDasharray="6 6" strokeWidth={3} dot={false} />
              )}
              { showPrediction && (predictionMode === 'optimista' || predictionMode === 'todos') && (
                <Line type="monotone" dataKey="pred_optimista" name="Predicci√≥n 2024 (Optimista)" stroke="#06b6d4" strokeDasharray="4 6" strokeWidth={3} dot={false} />
              )}
              { showPrediction && (predictionMode === 'pesimista' || predictionMode === 'todos') && (
                <Line type="monotone" dataKey="pred_pesimista" name="Predicci√≥n 2024 (Pesimista)" stroke="#ef4444" strokeDasharray="2 6" strokeWidth={3} dot={false} />
              )}
            </LineChart>
          </ResponsiveContainer>
        ) : (
          <div className="no-data">
            <Upload size={64} /><p>No hay datos disponibles. Sube o coloca el CSV en /public/data/</p>
          </div>
        )}
      </section>

      <footer>Simulador COVID-19 Oaxaca ‚Ä¢ Archivos CSV en /public/data/</footer>
    </div>
  );
};

export default OaxacaCovidSimulator;
